mapper.select:
SELECT test.idx, test.name, test.title, test.content, fileupload.file_real_name FROM test LEFT OUTER JOIN fileupload ON test.idx = fileupload.idx
WHERE ${type} LIKE '%'||#{keyword}||'%'
ORDER BY idx asc LIMIT #{limit} OFFSET #{offset};

mapper.update:
UPDATE test
SET (title, content)=(#{boardTitle}, #{boardContent})
WHERE idx = #{boardIdx}
